import $ from 'jquery';           // Import jQuery
import 'bootstrap';               // Import Bootstrap
import { DataTable } from 'simple-datatables'; // Correct import for simpleDatatables
import * as echarts from 'echarts';             // Correct import for echarts
import Quill from 'quill';                  // Quill editor
import tinymce from 'tinymce';              // TinyMCE editor

// Ensure jQuery is globally available (for plugins that rely on it)
window.$ = $;
window.jQuery = $;

(function($) {
  "use strict";

  // Easy selector helper function
  const select = (el, all = false) => {
    el = el.trim();
    return all ? $(el) : $(el).eq(0);
  };

  // Easy event listener function
  const on = (type, el, listener, all = false) => {
    const elements = select(el, all);
    elements.on(type, listener); // Attach event to all matched elements if needed
  };

  // Sidebar toggle
  if (select('.toggle-sidebar-btn').length) {
    on('click', '.toggle-sidebar-btn', function() {
      $('body').toggleClass('toggle-sidebar'); // Toggle the sidebar class
    });
  }

  // Rest of your code follows...
})(jQuery);
